import { useState } from "react";

const Form = (addCita) => {
    const [formData, setFormData] = useState({
        mascotName: '',
        ownerName: '',
        date: '',
        time: '',
        sintomas: ''
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = () => {
        addCita(formData);
        setFormData({
            mascotName: '',
            ownerName: '',
            date: '',
            time: '',
            sintomas: ''
        });
    };
    
    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="mascotName">Nombre mascota</label>
            <input type="text" value={formData.mascotName} placeholder="Nombre mascota" name="mascotName" onChange={handleChange} required/>

            <label htmlFor="ownerName">Nombre dueño</label>
            <input type="text" value={formData.ownerName} placeholder="Nombre dueño" name="ownerName" onChange={handleChange} required/>

            <label htmlFor="date-input">Fecha</label>
            <input type="date" value={formData.date} name="date-input" onChange={handleChange} required/>

            <label htmlFor="time-input">Horario</label>
            <input type="time" value={formData.time} name="time-input" onChange={handleChange} required/>

            <label htmlFor="sintomas">Sintomas: </label>
            <textarea name='sintomas' rows={4} value={formData.sintomas} style={{resize: "none"}} onChange={handleChange}></textarea>
            <button type="submit" className="btn submit-btn">Agregar cita</button>
        </form>
    )
}

export default Form;
